(() => {
  const currencyConverterApp = angular.module('currencyConverterApp', []);

  window.currencyConverterApp = currencyConverterApp;
})();

(() => {
  const currencyConverterApp = angular.module('currencyConverterApp');

  currencyConverterApp.controller('currencyController', ['$scope', 'commissions', 'currencyUAH', 'currencyService', function currencyController($scope, commissions, currencyUAH, currencyService) {
    $scope.currencies = [];
    $scope.currencyToSell = {};
    $scope.currencyToBuy = {};
    $scope.crossPrice = null;

    $scope.commissions = commissions;

    currencyService.updatePrices().then((response) => {
      $scope.currencies = response.data;
      $scope.currencies.push(currencyUAH);
    });

    $scope.calcCrossPrice = () => {
      // eslint-disable-next-line max-len
      $scope.crossPrice = currencyService.calcCrossPrice($scope.currencyToSell, $scope.currencyToBuy, $scope.currencies);
    };

    $scope.setActive = (currency) => {
      $scope.currencyActive = currency;
    };

    $scope.calcSumToReceive = () => {
      $scope.sumToReceive = $scope.sumToPay * $scope.crossPrice * (1 - $scope.commission / 100);
      $scope.sumToReceive = +$scope.sumToReceive.toFixed(2);
    };

    $scope.calcSumToPay = () => {
      $scope.sumToPay = $scope.sumToReceive / $scope.crossPrice / (1 - $scope.commission / 100);
      $scope.sumToPay = +$scope.sumToPay.toFixed(2);
    };

    $scope.calcSums = () => {
      if ($scope.currencyActive === $scope.currencyToSell) {
        $scope.calcSumToReceive();
      } else {
        $scope.calcSumToPay();
      }
    };

    $scope.swapCurrencies = () => {
      [$scope.currencyToSell, $scope.currencyToBuy] = [$scope.currencyToBuy, $scope.currencyToSell];
    };

    $scope.clearSums = () => {
      $scope.sumToReceive = null;
      $scope.sumToPay = null;
    };
  }]);
})();

/* eslint-disable func-names */
/* eslint-disable implicit-arrow-linebreak */
(() => {
  const currencyConverterApp = angular.module('currencyConverterApp');

  currencyConverterApp.value('commissions', [0, 1, 2, 3, 4, 5]);

  currencyConverterApp.value('urlPrivate', 'https://api.privatbank.ua/p24api/pubinfo?json&exchange&coursid=5');

  currencyConverterApp.value('currencyUAH', {
    ccy: 'UAH', base_ccy: 'UAH', buy: 1, sale: 1,
  });

  currencyConverterApp.service('currencyService', ['$http', 'urlPrivate', function ($http, urlPrivate) {
    this.updatePrices = () =>
      $http({
        method: 'GET',
        url: urlPrivate,
      });


    this.calcCrossPrice = (currencyToSell, currencyToBuy, currencies) => {
      let ccyToSell = currencyToSell;
      let ccyToBuy = currencyToBuy;

      let crossPrice = 1;

      if (!ccyToSell || !ccyToBuy) return crossPrice;

      if (ccyToSell.base_ccy === ccyToBuy.ccy) {
        crossPrice = +ccyToSell.buy;
        return crossPrice;
      }

      while (ccyToSell.ccy !== ccyToBuy.ccy) {
        crossPrice = crossPrice * +ccyToSell.buy / ccyToBuy.sale;

        // eslint-disable-next-line no-loop-func
        ccyToSell = currencies.find(item => item.ccy === ccyToSell.base_ccy);
        // eslint-disable-next-line no-loop-func
        ccyToBuy = currencies.find(item => item.ccy === ccyToBuy.base_ccy);
      }

      return crossPrice;
    };
  }]);
})();

/* eslint-disable implicit-arrow-linebreak */
(() => {
  const currencyConverterApp = angular.module('currencyConverterApp');

  currencyConverterApp.filter('filterCurrencies', () =>
    (currencies, usedCurrency) =>
      currencies.filter(currency => currency !== usedCurrency));
})();
